def Fibonacci(n): 
    if n<0: 
        print("输入不正确") 
    # 第一个斐波那契数是0 
    elif n==1: 
        return 0
    # 第二个斐波那契数是1 
    elif n==2: 
        return 1
    else: 
        return Fibonacci(n-1)+Fibonacci(n-2) 

